		$line =~ s/\xcb\x9a//g;#deletes ring above sign
		$line =~ s/\xce\x91/a/g;#changes greek capital alphas to a
		$line =~ s/\xce\x92/b/g;#changes greek capital betas to b
		$line =~ s/\xce\xb1/a/g;#changes greek alphas α to a
