	chomp $line;
	$line =~ s/\t\t/\t/g;
	$line =~ s/$/\t/;
	my @values = split /\t/, $line;
	my @filtered = uniq(@values);
	@filtered = sort(@filtered);
	print OUT @filtered; # prints to $temp3, temp3.fermused.txt
