		map {s/ pm//gi; } @celllengthdata; #gets rid of 'pm', which is a pdf-to-text conversion error
		map {s/pm//gi; } @celllengthdata; #gets rid of 'pm', which is a pdf-to-text conversion error
		map {s/p//gi; } @celllengthdata; #gets rid of 'pm', which is a pdf-to-text conversion error
		map {s/mm//gi; } @celllengthdata; #gets rid of 'mm', which is a pdf-to-text conversion error
		map {s/m//gi; } @celllengthdata; #gets rid of 'mm', which is a pdf-to-text conversion error
		map {s/~//gi; } @celllengthdata; #gets rid of ~ 
		map {s/\s//gi; } @celllengthdata; #gets rid of 'space'
		map {s/^,//; } @celllengthdata; #gets rid of first comma in the output data
		map {s/,$//; } @celllengthdata; #gets rid of last comma in the output data
	my @mediancelllength;
	my $temp;
	foreach $temp (@celllengthdata) {
		if (($temp eq "") || (not defined $temp)) {
			next;
			}
		if ($temp =~ /\xc3\x97/) {#looks for an x in the output - indicates that the data contains width x length
			$temp =~ s/\xc3\x97/x/g; #remove the first range of values up to the x
			$temp =~ s/^[^x]*x//g; #remove the first range of values up to and including the x
			}
		elsif ($temp =~ /x/) {#looks for an x in the output - indicates that the data contains width x length
			$temp =~ s/^[^x]*x//g; #remove the first range of values up to and including the x
			}
		elsif ($temp =~ /±.*,/) { 
			map {s/,.*//g; } @celllengthdata; # get rid of the additional values
			}
		elsif ($temp =~ /-.*,/) { 
			map {s/,.*//g; } @celllengthdata; # get rid of the additional values
			}
		elsif ($temp =~ /,/) { # there are multiple values of mol gc data
			map {s/,.*//g; } @celllengthdata; # get rid of the additional values
			@mediancelllength = @celllengthdata;
			}
		elsif ($temp =~ /-/) { # mol gc data exists as a range
			$temp =~ s/[^[:print:]]//g; #delete non-printable chars
			if ($temp =~ /^-\s/) { # if the first value is missing delete the dash and treat it as a single value
				$temp =~ s/^-\s//;
				next;
				}
			else {
				my @medianvals = split /-/, $temp;
				my $mval1 = $medianvals[0];
				my $temp1 = "$mval1" + 0.0; # ensures that the value is a digit
				my $mval2 = $medianvals[1];
				my $temp2 = "$mval2" + 0.0; # ensures that the value is a digit
				my @temp3 = ($temp1, $temp2);
				my @mval3 = median(@temp3);
				my $mval4 = "@mval3" + 0.0; # ensures that the value is a digit
				@mediancelllength = "$mval4" + 0.0; 
				}
			}
		elsif ($temp =~ /\xc2\xb1/){ # mol gc data exists as a median with error
			my @celllengthdata = split /\xc2\xb1/, $temp;
			$temp =$celllengthdata[0];
			@mediancelllength = "$temp" + 0.0; # ensures that mediangc is a digit
				}
		else { # molgc single value 
			$temp =~ s/[^[:print:]]//g; #delete non-printable chars
			@mediancelllength = $temp;
			next;
			}
		}
		local $, = "";
		print OUT @taxnames, " ", @mediancelllength, "\n"; # prints to $nexusoutfile meancelllength.nex
	}
	print OUT "\n\n\;\n\nEND\;\n";
	print $outmessage;
	unlink $rawmatrix;
	unlink $unixfile;
	unlink $unixfile2;
	}

